clear allN=1000;x=normrnd(1,2,1,N); % mu=1, sigma=2pa=mean((0.5<x) & (x<1)) %P(0.5<X<1)=normcdf(1,1,2)-normcdf(0.5,1,2)pb=1-normcdf(1,1,2)             %P(X>1)e=mean(x) %estimare valoare mediev=var(x) % estimare variantaclose allfiguret=min(x)-1:0.1:max(x)+1;d=normpdf(t,1,2);plot(t,d,'r.')title('Functia de densitate')figurer=normcdf(t,1,2);plot(t,r,'b.')title('Functia de repartitie')