function out =  practic()    #Punctul a - teoretic    Pzero = 0.7 ^ 4;    Punu = 4 * (0.7 ^ 3) * 0.3;    Pteoretic = 1 - (Pzero + Punu)        #Punctul a    fav = 0;    for i=1:10000      baterie1 = (rand<=0.3);      baterie2 = (rand<=0.3);      baterie3 = (rand<=0.3);      baterie4 = (rand<=0.3);      rez = [baterie1 baterie2 baterie3 baterie4];      s = sum(rez);      if s >= 2        fav = fav + 1;      end    end        PpctA = fav / 10000                #Punctul b     fav = 0;     for i=1:10000      baterie1 = (rand<=0.3);      baterie2 = (rand<=0.3);      baterie3 = (rand<=0.3);      baterie4 = (rand<=0.3);      rez = [baterie1 baterie2 baterie3 baterie4];      if baterie1 == 1 && (baterie2 == 1 || baterie3 == 1) && baterie4 == 1         fav = fav + 1;      end    end        PpctB = fav / 10000            #Punctul c            fav = 0;    for i = 1:1000      b1 = unifrnd(0,4);      b2 = unifrnd(0,4);      b4 = unifrnd(0,4);      b3 = unifrnd(0,4);            bparalel = [b2 b3];            bparalel = max(bparalel);           bfinal = [b1 bparalel b4];           bfinal = min(bfinal);            if bfinal >= 2        fav = fav + 1;            end     end         PpctC = fav /1000    end